package dev.kdh.ani;

public class threedays {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        
              System.out.println("5에서 1까지의 수입니다.");
        
       for(int i=5; i>=1; i--){
        	
        	System.out.println(i+"\t");
        	
        }
	}

}
